from buss_dual_referral

  1=1

    AND   pat_name like CONCAT('%', #{busDualReferral.patName}, '%')


    AND   ref_status =  #{busDualReferral.refStatus,jdbcType=VARCHAR}

  /*为空 查询准入转出列表*/

    AND   (org_id_from =  #{busDualReferral.orgIdFrom,jdbcType=VARCHAR}
    OR org_id_to =  #{busDualReferral.orgIdFrom,jdbcType=VARCHAR})

   /*转出*/

    AND   org_id_from =  #{busDualReferral.orgIdFrom,jdbcType=VARCHAR}

  /* 转入*/

    AND   org_id_to =  #{busDualReferral.orgIdFrom,jdbcType=VARCHAR}


