SELECT COUNT(1) num , DATE_FORMAT(cmmit_date,'%Y%m') month
from buss_dual_referral where org_id_from =   #{orgIdFrom,jdbcType=VARCHAR}
and YEAR(cmmit_date) = #{cmmit_date,jdbcType=VARCHAR}
GROUP BY month

select month(cmmit_date) month, count(1) count
from buss_dual_referral
where YEAR (cmmit_date) = #{year}   

and org_id_from=#{hosId}


and org_id_to=#{hosId}

GROUP BY MONTH(cmmit_date)
